package week1.day2;

public class LearnIfCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=7;
		int b=7;
		//7<7
		
		if(a<b) {
			System.out.println(a+" is smaller than "+b);
		}
		
		
		else {
			System.out.println(a+" is greater than "+b);
		}
		
		
		
	}

}
